import {
    FETCH_ALL_USERS,
    FETCH_USER_BY_ID,
    FETCH_USER_BY_EMAIL
} from '../actions/userAction';

const initialState = {
    allUsers: [],
    userId: '',
    
};

export default function (state = initialState, action) {

    switch (action.type) {
        case FETCH_ALL_USERS:
            return {
                ...state,
                allUsers: action.payload
            }
        case FETCH_USER_BY_ID:
            return {
                ...state,
                oneUser: action.payload
            }
        case FETCH_USER_BY_EMAIL:
            return {
                ...state,
                oneUser: action.payload
            }
    }
    return state;
};
