import React from 'react';
import './searchPage.css';

import { useHistory } from 'react-router-dom';

import { useState } from 'react';

import { useDispatch } from 'react-redux';
import * as userAction from '../../redux/actions/userAction';

import SearchIcon from '@material-ui/icons/Search';

import { Button, Form } from 'semantic-ui-react';

export default function SearchPage(props) {
    const dispatch = useDispatch();

    const history = useHistory();

    const [searchItem, setSearchItem] = useState('');

    const handleKeyPress = (e) => {
        if (e.key === "Enter") {
            e.preventDefault();
            dispatch(userAction.fetchUserByEmail(searchItem))
            setSearchItem('');
            history.push('/result');
        };
    };

    const handleTextChange = (e) => {
        setSearchItem(e.target.value);
    }

    return (
        <div className="searchPage">
            <img
                src="https://imanpublication.com/wp-content/uploads/2021/06/logo-iman-21-signature.png"
                width="300"
                alt="logoIman"
            />
            <Form>
                <Form.Field>
                            <input
                                placeholder='Title'
                                onChange={handleTextChange}
                                value={searchItem}
                                onKeyPress={handleKeyPress} />
                        </Form.Field>
            </Form>
            <div className="searchContainer">
                <p className="typeInEmail">Type-in your ImanShoppe e-mail to get your Signature</p>
                
            </div>
        </div>
    )
}
